import React, { useState } from "react";
import Header from "../header/Header";
import Footer from "../Footer/Footer";
import styles from "./Main.module.css";

const Main = () => {
    const [isDarkMode, setIsDarkMode] = useState(true);  // Default dark mode
    const [selectedLanguage, setSelectedLanguage] = useState('UA'); // Language state

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);  // Toggle between dark and light themes
    };

    const handleLanguageChange = (event) => {
        setSelectedLanguage(event.target.value);  // Update selected language
    };

    return (
        <div className={`${styles.App} ${isDarkMode ? styles.dark_mode : styles.light_mode}`}>
            <Header 
                isDarkMode={isDarkMode} 
                toggleTheme={toggleTheme} 
                selectedLanguage={selectedLanguage}
                handleLanguageChange={handleLanguageChange} 
            />
            <main className={styles.main_v_3}> 
                {/* Content goes here */}
            </main>
            <Footer /> {/* Footer will automatically get dark or light styles from parent */}
        </div>
    );
};

export default Main;
