const Main = () => {
    const [isDarkMode, setIsDarkMode] = useState(true);  // Темна тема за замовчуванням
    const [selectedLanguage, setSelectedLanguage] = useState('UA'); // Стан для вибраної мови

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);  // Перемикає між темною та світлою темою
    };

    const handleLanguageChange = (event) => {
        setSelectedLanguage(event.target.value);  // Оновлюємо вибрану мову
    };

    return (
        <div className={`${styles.App} ${isDarkMode ? styles.dark_mode : styles.light_mode}`}>
            <Header 
                isDarkMode={isDarkMode} 
                toggleTheme={toggleTheme} 
                selectedLanguage={selectedLanguage}
                handleLanguageChange={handleLanguageChange} 
            />
            <main className={styles.main_v_3}> 
            </main>
            <Footer /> 
        </div>
    );
};
